from sqlalchemy import Column, Integer, String, Float, ForeignKey, Index
from sqlalchemy.orm import relationship
from app.database import Base

class Supplies(Base):
    __tablename__ = "Supplies"
    supply_id = Column(Integer, primary_key=True, index=True)
    name = Column(String, nullable=False)
    purchase_price = Column(Float)
    size_weight = Column(String)
    producer = Column(String)
    category_id = Column(Integer, ForeignKey("Category.category_id"), index=True)
    image_url = Column(String, nullable=False, default="data:image/jpeg;base64,")

class Category(Base):
    __tablename__ = "Category"
    category_id = Column(Integer, primary_key=True, index=True)
    name = Column(String, unique=True, nullable=False)
    parent_id = Column(Integer, ForeignKey("Category.category_id"), nullable=True)
    parent = relationship("Category", remote_side=[category_id], backref="subcategories")

# Add explicit indexes if needed
Index("ix_supplies_supply_id", Supplies.supply_id)
Index("ix_supplies_category_id", Supplies.category_id)
